--
-- Create procedure `Proc_OverTime_Filter`
--
CREATE 
	DEFINER = 'root'@'localhost'
PROCEDURE Proc_OverTime_Filter(IN v_Where TEXT, -- Tên nhân viên muốn tìm
IN v_Offset INT, -- bản ghi bắt đầu
IN v_Limit INT, -- Số bản ghi trên 1 trang
IN v_Status INT, -- Trạng thái
IN v_Departmentid TEXT -- Id phòng ban
, -- Trạng thái
IN v_EmployeeID VARCHAR(255))
  COMMENT 'Phân trang theo người nộp đơn, trạng thái, đơn vị'
BEGIN
  IF IFNULL(v_Where, '') = '' THEN
    SET v_Where = '';
  END IF;

  IF v_Status = 0 THEN
  BEGIN
SELECT
  *
FROM overtime o
WHERE (o.FullName LIKE CONCAT('%', v_Where, '%')
OR o.EmployeeCode LIKE CONCAT('%', v_Where, '%'))
AND o.MISACode LIKE CONCAT(v_Departmentid, '%')
AND (o.EmployeeId = v_EmployeeID
OR o.ApprovalId = v_EmployeeID)
ORDER BY o.ModifiedDate DESC
LIMIT v_Limit
OFFSET v_Offset;

SELECT
  COUNT(*)
FROM overtime o
WHERE (o.FullName LIKE CONCAT('%', v_Where, '%')
OR o.EmployeeCode LIKE CONCAT('%', v_Where, '%'))
AND o.MISACode LIKE CONCAT(v_Departmentid, '%')
AND (o.EmployeeId = v_EmployeeID
OR o.ApprovalId = v_EmployeeID);
END;
ELSE
  BEGIN
SELECT
  *
FROM overtime o
WHERE (o.FullName LIKE CONCAT('%', v_Where, '%')
OR o.EmployeeCode LIKE CONCAT('%', v_Where, '%'))
AND o.Status = v_Status
AND o.MISACode LIKE CONCAT(v_Departmentid, '%')
AND (o.EmployeeId = v_EmployeeID
OR o.ApprovalId = v_EmployeeID)
ORDER BY o.ModifiedDate DESC
LIMIT v_Limit
OFFSET v_Offset;

SELECT
  COUNT(*)
FROM overtime o
WHERE (o.FullName LIKE CONCAT('%', v_Where, '%')
OR o.EmployeeCode LIKE CONCAT('%', v_Where, '%'))
AND o.Status = v_Status
AND o.MISACode LIKE CONCAT(v_Departmentid, '%')
AND (o.EmployeeId = v_EmployeeID
OR o.ApprovalId = v_EmployeeID);
END;
END IF;
END
$$

